from sage.all import *
from Crypto.Cipher import AES
from hashlib import sha256

shares = [(41458, ), (3389, ), (20016, ), (50683, ), (6445, ), (1359, ), (45286, ), (5649, )]
ctxt = b'G$\xf5\x9e\xa9\xb1e\xb5\x86w\xdfz\xbeP\xecJ\xb8wT<<\x84\xc5v\xb4\x02Z\xa4\xed\x8fB\x00[\xc0\x02\xf9\xc0x\x16\xf9\xa4\x02\xb8\xbb'
nonce_ctr = b'\x8f\xa5z\xb4mZ\x97\xe9'

def polyeval(poly, x):
    return sum([a * x**i for i, a in enumerate(poly)])

poly = [0] * 129

for i in range(129):
    CRT_1 = [0] * 8
    CRT_2 = [0] * 8
    for j in range(8):
        CRT_1[j] = shares[j][1] % shares[j][0]
        CRT_2[j] = shares[j][0]
    tt = CRT(CRT_1, CRT_2)
    assert 0 <= int(tt) < (1 << 64)
    poly[i] = int(tt)
    for j in range(8):
        shares[j] = (shares[j][0], (shares[j][1] - poly[i]) // shares[j][0])

secret = polyeval(poly, 0x48763)
key = sha256(str(secret).encode()).digest()[:16]
cipher = AES.new(key, AES.MODE_CTR, nonce = nonce_ctr)

print(cipher.decrypt(ctxt))
